<?php 
	/*	
	*	Goodlayers Core Plugin Filter
	*	---------------------------------------------------------------------
	*	This file contains the script to includes necessary function
	* 	for compatibility with goodlayers core plugin
	*	---------------------------------------------------------------------
	*/

	// add body class for page builder
	add_filter('gdlr_core_page_builder_body_class', 'attorna_page_builder_body_class');
	if( !function_exists('attorna_page_builder_body_class') ){
		function attorna_page_builder_body_class( $classes ) {
			return $classes . ' attorna-body';
		}
	}
	add_filter('gdlr_core_sidebar_class', 'attorna_gdlr_core_sidebar_class');
	if( !function_exists('attorna_gdlr_core_sidebar_class') ){
		function attorna_gdlr_core_sidebar_class( $classes ) {
			return $classes . ' attorna-sidebar-area';
		}
	}

	// Set the neccessary function to be used in the theme
	add_action('after_setup_theme', 'attorna_goodlayers_core_setup');
	if( !function_exists( 'attorna_goodlayers_core_setup' ) ){
		function attorna_goodlayers_core_setup(){

			// to set the goodlayers core plugin pagination
			add_filter('gdlr_core_pagination_style', 'attorna_gdlr_core_pagination_style');
			add_filter('gdlr_core_pagination_align', 'attorna_gdlr_core_pagination_align');

			// set the default social share option
			add_filter('gdlr_core_social_share_list', 'attorna_gdlr_core_social_share_list');

			// widget title navigation
			add_filter('gdlr_core_widget_title_nav', 'attorna_gdlr_core_widget_title_nav');

			// single portfolio
			add_filter('gdlr_core_custom_post_slug', 'attorna_gdlr_core_custom_post_slug', 10, 2);

			add_filter('gdlr_core_portfolio_single_nav', 'attorna_gdlr_core_portfolio_single_nav');
			add_filter('gdlr_core_portfolio_single_nav_same_term', 'attorna_gdlr_core_portfolio_single_nav_same_term');
			add_filter('gdlr_core_portfolio_single_related', 'attorna_gdlr_core_portfolio_single_related');

			// srcset
			add_filter('gdlr_core_enable_srcset', 'attorna_gdlr_core_enable_srcset');

			// filter for portfolio icon hover
			add_filter('gdlr_core_portfolio_hover_icon_class', 'attorna_gdlr_core_portfolio_hover_icon_class');

			// youtube cookies
			add_filter('gdlr_core_privacy_settings', 'attorna_gdlr_core_privacy_settings');
		}
	}

	// youtube no cookies
	if( !function_exists('attorna_gdlr_core_privacy_settings') ){
		function attorna_gdlr_core_privacy_settings(){
			if( function_exists('gdlr_core_get_privacy_options') ){
				$privacy_options = gdlr_core_get_privacy_options(1) + gdlr_core_get_privacy_options(2);
				$privacy_settings = array();
				foreach( $privacy_options as $privacy_option_slug => $privacy_option ){
					$privacy_settings[$privacy_option_slug] = attorna_get_option('plugin', $privacy_option_slug, 
						(empty($privacy_option['default'])? '': $privacy_option['default']));
				}

				return $privacy_settings;
			}
			
			return array();
		}
	}

	// portfolio hover icon class
	if( !function_exists('attorna_gdlr_core_portfolio_hover_icon_class') ){
		function attorna_gdlr_core_portfolio_hover_icon_class( $type = '' ){
			if( $type == 'image' ){
			 	return attorna_get_option('general', 'portfolio-icon-hover-image', '');
			}else if( $type == 'video' ){
				return attorna_get_option('general', 'portfolio-icon-hover-video', '');
			}else if( $type == 'link' ){
				return attorna_get_option('general', 'portfolio-icon-hover-link', '');
			}
		}
	}

	// to enable srcset
	if( !function_exists('attorna_gdlr_core_enable_srcset') ){
		function attorna_gdlr_core_enable_srcset( $enable ){
			$enable_srcset = attorna_get_option('plugin', 'enable-srcset', 'disable');
			return ($enable_srcset == 'enable');
		}
	}
			
	// for goodlayers core pagination
	if( !function_exists('attorna_gdlr_core_pagination_style') ){
		function attorna_gdlr_core_pagination_style( $style ) {
			return attorna_get_option('general', 'pagination-style', $style);
		}
	}
	if( !function_exists('attorna_gdlr_core_pagination_align') ){
		function attorna_gdlr_core_pagination_align( $align ) {
			return attorna_get_option('general', 'pagination-align', $align);
		}
	}			

	// custom post type slug
	if( !function_exists('attorna_gdlr_core_custom_post_slug') ){
		function attorna_gdlr_core_custom_post_slug( $slug, $type = '' ) {
			if( empty($type) ){ $type = $slug; }

			if( $type == 'portfolio' ){
				$slug = attorna_get_option('general', 'portfolio-slug', $slug);
			}else if( $type == 'portfolio_category' ){
				$slug = attorna_get_option('general', 'portfolio-category-slug', $slug);
			}else if( $type == 'portfolio_tag' ){
				$slug = attorna_get_option('general', 'portfolio-tag-slug', $slug);
			}else if( $type == 'personnel' ){
				$slug = attorna_get_option('general', 'personnel-slug', $slug);
			}else if( $type == 'personnel_category' ){
				$slug = attorna_get_option('general', 'personnel-category-slug', $slug);
			}
			
			return $slug;
		}
	}

	// single portfolio
	if( !function_exists('attorna_gdlr_core_portfolio_single_nav') ){
		function attorna_gdlr_core_portfolio_single_nav( $check ) {
			return attorna_get_option('general', 'enable-single-portfolio-navigation', $check);
		}
	}
	if( !function_exists('attorna_gdlr_core_portfolio_single_nav_same_term') ){
		function attorna_gdlr_core_portfolio_single_nav_same_term( $check ) {
			return attorna_get_option('general', 'enable-single-portfolio-navigation-in-same-tag', $check);
		}
	}
	if( !function_exists('attorna_gdlr_core_portfolio_single_related') ){
		function attorna_gdlr_core_portfolio_single_related( $args ) {

			$enble_related_portfolio = attorna_get_option('general', 'enable-related-portfolio', 'enable');
			if( $enble_related_portfolio == 'enable' ){
				$args['portfolio-style'] = attorna_get_option('general', 'related-portfolio-style', 'grid');
				$args['num-fetch'] = attorna_get_option('general', 'related-portfolio-num-fetch', 4);
				$args['column-size'] = attorna_get_option('general', 'related-portfolio-column-size', 15);
				$args['thumbnail-size'] = attorna_get_option('general', 'related-portfolio-thumbnail-size', 'medium');
				$args['excerpt-number'] = attorna_get_option('general', 'related-portfolio-num-excerpt', 20);
				$args['column-size'] = attorna_get_option('general', 'related-portfolio-column-size', 15);

				return $args;
			}else{
				return array();
			}
		}
	}

	// set default social share option
	if( !function_exists('attorna_gdlr_core_social_share_list') ){
		function attorna_gdlr_core_social_share_list( $social ) {

			$social['facebook'] = attorna_get_option('general', 'blog-social-facebook', $social['facebook']);
			$social['linkedin'] = attorna_get_option('general', 'blog-social-linkedin', $social['linkedin']);
			$social['google-plus'] = attorna_get_option('general', 'blog-social-google-plus', $social['google-plus']);
			$social['pinterest'] = attorna_get_option('general', 'blog-social-pinterest', $social['pinterest']);
			$social['stumbleupon'] = attorna_get_option('general', 'blog-social-stumbleupon', $social['stumbleupon']);
			$social['twitter'] = attorna_get_option('general', 'blog-social-twitter', $social['twitter']);
			$social['email'] = attorna_get_option('general', 'blog-social-email', $social['email']);

			return $social;
		}
	}	

	// add filter to register custom fonts for font lists 
	add_filter('gdlr_core_custom_uploaded_font', 'attorna_register_custom_uploaded_font');
	if( !function_exists('attorna_register_custom_uploaded_font') ){
		function attorna_register_custom_uploaded_font( $custom_fonts ){

			$custom_font = attorna_get_option('typography', 'font-upload');
			if( !empty($custom_font) ){
				foreach($custom_font as $font_option){
					$custom_fonts[$font_option['name']] = array(
						'eot' => $font_option['eot'],
						'ttf' => $font_option['ttf'],
					);
				}
			}		
			
			return $custom_fonts;
			
		} // attorna_register_custom_uploaded_font
	} // function_exists

	// format widget title
	if( !function_exists('attorna_gdlr_core_widget_title_nav') ){
		function attorna_gdlr_core_widget_title_nav( $title_nav ){
			return '</span><span class="gdlr-core-flexslider-nav gdlr-core-plain-style gdlr-core-size-widget">';
		}
	}
	
	// add page builder to post
	add_filter('gdlr_core_page_builder_post_type', 'attorna_gdlr_core_page_builder_post_type');
	if( !function_exists('attorna_gdlr_core_page_builder_post_type') ){
		function attorna_gdlr_core_page_builder_post_type( $post_types ){
			$post_types[] = 'post';
			return $post_types;
		}
	}

	// set lightbox type
	add_filter('gdlr_core_lightbox_type', 'attorna_gdlr_core_lightbox_type');
	if( !function_exists('attorna_gdlr_core_lightbox_type') ){
		function attorna_gdlr_core_lightbox_type(){
			$lightbox_type = attorna_get_option('plugin', 'lightbox', 'ilightbox');

			if( $lightbox_type == 'ilightbox' ){
				$ilightbox_skin = attorna_get_option('plugin', 'ilightbox-skin', 'dark');
				return $lightbox_type . '-' . $ilightbox_skin;
			}else{
				return $lightbox_type;
			}
		}
	}
	add_filter('body_class', 'attorna_lightbox_body_class');
	if( !function_exists('attorna_lightbox_body_class') ){
		function attorna_lightbox_body_class($class){
			$link_to_lightbox = attorna_get_option('plugin', 'link-to-lightbox', 'enable');
			if( $link_to_lightbox == 'enable' ){
				$class[] = 'gdlr-core-link-to-lightbox';
			}
			return $class;
		}
	}

	add_filter('gdlr_core_lightbox_video_autoplay', 'attorna_lightbox_video_autoplay');
	if( !function_exists('attorna_lightbox_video_autoplay') ){
		function attorna_lightbox_video_autoplay($autoplay){
			$video_autoplay = attorna_get_option('plugin', 'lightbox-video-autoplay', 'enable');
			return ($video_autoplay == 'enable')? true: false;
		}
	}
	
	
